package com.sina.parkingManagement.repository;import com.sina.parkingManagement.model.ParkingDetails;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.domain.Specification;import org.springframework.data.jpa.repository.JpaSpecificationExecutor;import org.springframework.data.repository.PagingAndSortingRepository;import java.util.List;public interface ParkingDetailsInterface extends PagingAndSortingRepository<ParkingDetails, Long>, JpaSpecificationExecutor<ParkingDetails> {    Page<ParkingDetails> findAllByVehicle_PlateNumber(String plateNumber, Pageable pageable);    List<ParkingDetails> findAll(Specification<ParkingDetails> parkingDetailSpecification);}