package com.sina.parkingManagement.service.impl;import com.sina.parkingManagement.common.exception.NotFoundException;import com.sina.parkingManagement.model.Parking;import com.sina.parkingManagement.repository.ParkingInterface;import com.sina.parkingManagement.service.IParkingService;import lombok.AllArgsConstructor;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.data.domain.Sort;import org.springframework.stereotype.Service;import java.util.Optional;@AllArgsConstructor@Servicepublic class ParkingService implements IParkingService {    private final ParkingInterface parkingInterface;    @Override    public Parking addParking(Parking parking) {        return parkingInterface.save(parking);    }    @Override    public void deleteParking(Long parkingId) {        Parking parking = getParking(parkingId);        parkingInterface.delete(parking);    }    @Override    public Parking updateParking(Parking parking) {        Parking savedBefore = getParking(parking.getId());        savedBefore.setBasePrice(parking.getBasePrice());        return parkingInterface.save(savedBefore);    }    @Override    public Parking getParking(Long parkingId) {        Optional<Parking> optionalParking = parkingInterface.findById(parkingId);        if (optionalParking.isPresent()) {            return optionalParking.get();        }        throw new NotFoundException("parking not found");    }    @Override    public Page<Parking> paging(Integer page, Integer size) {        return parkingInterface.findAll(PageRequest.of(page, size, Sort.by("id").ascending()));    }}