package com.sina.parkingManagement.service.impl;import com.sina.parkingManagement.common.exception.NotFoundException;import com.sina.parkingManagement.model.PriceRate;import com.sina.parkingManagement.repository.PriceRateInterface;import com.sina.parkingManagement.service.IPriceRateService;import lombok.AllArgsConstructor;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.data.domain.Sort;import org.springframework.stereotype.Service;import java.util.Optional;@AllArgsConstructor@Servicepublic class PriceRateService implements IPriceRateService {    private final PriceRateInterface priceRateInterface;    @Override    public PriceRate addPriceRate(PriceRate priceRate) {        return priceRateInterface.save(priceRate);    }    @Override    public void deletePriceRate(Long priceRateId) {        PriceRate priceRate = getPriceRate(priceRateId);        priceRateInterface.delete(priceRate);    }    @Override    public PriceRate updatePriceRate(PriceRate priceRate) {        PriceRate savedBefore = getPriceRate(priceRate.getId());        savedBefore.setHourlyRate(priceRate.getHourlyRate());        savedBefore.setDailyRate(priceRate.getDailyRate());        savedBefore.setMonthlyRate(priceRate.getMonthlyRate());        return priceRateInterface.save(savedBefore);    }    @Override    public PriceRate getPriceRate(Long priceRateId) {        Optional<PriceRate> optionalPriceRate = priceRateInterface.findById(priceRateId);        if (optionalPriceRate.isPresent()) {            return optionalPriceRate.get();        }        throw new NotFoundException("priceRate not found");    }    @Override    public Page<PriceRate> paging(Integer page, Integer size) {        return priceRateInterface.findAll(PageRequest.of(page, size, Sort.by("id").ascending()));    }}