package com.sina.parkingManagement.service.impl;import com.sina.parkingManagement.common.exception.NotFoundException;import com.sina.parkingManagement.model.Vehicle;import com.sina.parkingManagement.repository.VehicleInterface;import com.sina.parkingManagement.service.IVehicleService;import lombok.AllArgsConstructor;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.data.domain.Sort;import org.springframework.stereotype.Service;import java.util.Optional;@AllArgsConstructor@Servicepublic class VehicleService implements IVehicleService {    private final VehicleInterface vehicleInterface;    @Override    public Vehicle addVehicle(Vehicle vehicle) {        return vehicleInterface.save(vehicle);    }    @Override    public void deleteVehicle(Long vehicleId) {        Vehicle vehicle = getVehicle(vehicleId);        vehicleInterface.delete(vehicle);    }    @Override    public Vehicle updateVehicle(Vehicle vehicle) {        Vehicle savedBefore = getVehicle(vehicle.getId());        savedBefore.setPlateNumber(vehicle.getPlateNumber());        savedBefore.setVehicleType(vehicle.getVehicleType());        return vehicleInterface.save(savedBefore);    }    @Override    public Vehicle getVehicle(Long vehicleId) {        Optional<Vehicle> optionalVehicle = vehicleInterface.findById(vehicleId);        if (optionalVehicle.isPresent()) {            return optionalVehicle.get();        }        throw new NotFoundException("vehicle not found");    }    @Override    public Page<Vehicle> paging(Integer page, Integer size) {        return vehicleInterface.findAll(PageRequest.of(page, size, Sort.by("id").ascending()));    }}